import { apiSlice } from './apiSlice';
import { ORDERS_URL, PAYPAL_URL } from '../constants';

export const orderSlice = apiSlice.injectEndpoints({
    endpoints: (builder) => ({
      createOrder: builder.mutation({
        query: (order) => ({
          url: ORDERS_URL,
          method: 'POST',
          body: {...order},
        }),
      }),
    }),
  });
  
  export const {
    useCreateOrderMutation,
    useGetOrderDetailsQuery,
  } = orderSlice;
